#' @title finalrep
#' @name finalrep
#' @description Processing and reporting GreenFeed data - final report
#'
#' @param Exp Study name.
#' @param Unit The unit number/s of the GreenFeed.
#' @param Start_Date Start date of the study.
#' @param End_Date End date of the study.
#' @param Final_report List with final report/s generated by C-Lock.
#'
#' @return An excel file with daily data and a PDF report with description of methane data.
#'
#' @examples
#' \dontrun{
#' Exp <- "Test_study"
#' Unit <- "577,578"
#' Start_Date <- "2023-01-01"
#' End_Date <- "2023-04-01"
#'
#' # Please replace with the final report/s from C-Lock with the finalized GreenFeed data.
#' ## In case you have multiple reports from the same study you can combine them
#' Final_report <- list("/Users/CLock_finalreport1.xlsx", "/Users/CLock_finalreport2.xlsx")
#'
#' finalrep(Exp, Unit, Start_Date, End_Date, Final_report)
#' }
#'
#' @export finalrep
#'
#' @import readr
#' @import readxl
#' @import dplyr
#' @importFrom dplyr %>%
#' @import lubridate
#' @import rmarkdown
#' @import utils

utils::globalVariables(c("GoodDataDuration", "AirflowLitersPerSec"))

finalrep <- function(Exp = NA, Unit = NA, Start_Date = NA, End_Date = NA,
                     Final_report = list(NA), Plot_opt = "All") {

  # Function to read and process each file
  process_file <- function(file) {
    df <- readxl::read_excel(file, col_types = c("text", "text", "numeric", rep("date", 3), rep("numeric", 12), "text", rep("numeric", 6)))
    names(df)[1:14] <- c("RFID",
                         "FarmName",
                         "FeederID",
                         "StartTime",
                         "EndTime",
                         "GoodDataDuration",
                         "HourOfDay",
                         "CO2GramsPerDay",
                         "CH4GramsPerDay",
                         "O2GramsPerDay",
                         "H2GramsPerDay",
                         "H2SGramsPerDay",
                         "AirflowLitersPerSec",
                         "AirflowCf")


    df <- df %>%

      # Remove leading zeros from RFID col to match with IDs
      dplyr::mutate(RFID = gsub("^0+", "", RFID)) %>%

      # Change the format of good data duration column to minutes with two decimals
      dplyr::mutate(GoodDataDuration = round(lubridate::period_to_seconds(lubridate::hms(format(as.POSIXct(GoodDataDuration), "%H:%M:%S"))) / 60, 2)) %>%

      # Remove data with Airflow below the threshold (25 l/s)
      dplyr::filter(AirflowLitersPerSec >= 25,
                    StartTime >= Start_Date)

    return(df)
  }

  # Combine all final report files into one data frame
  df <- do.call(rbind, lapply(Final_report, process_file))

  # Create PDF report using Rmarkdown
  rmarkdown::render(system.file("FinalReportsGF.Rmd", package = "greenfeedR"),
                    output_file = file.path(getwd(), paste0("Report_", Exp, ".pdf")))

}
